$(document).ready(function() {
    AOS.init({
        disable: "mobile"
    }), $("[data-bs-hover-animate]").mouseenter(function() {
        var e = $(this);
        e.addClass("animated " + e.attr("data-bs-hover-animate"))
    }).mouseleave(function() {
        var e = $(this);
        e.removeClass("animated " + e.attr("data-bs-hover-animate"))
    })
});


$('.autoplay').slick({
    slidesToShow: 1,
   slidesToScroll: 1,
   autoplay: true,
   autoplaySpeed: 2000,
});
